mapper.select:
select *
from recipe_view
where recipe_status = 'N' and recipe_no = #{recipeNo}

select *
from Cookware_list left JOIN cookware_category using(cook_category_no)
where CW_RECIPE_NO = #{recipeNo}

mapper.update:
update recipe
set recipe_status = 'Y'
where recipe_no = #{recipeNo}

UPDATE RECIPE
SET RECIPE_NAME = #{recipeName}, RECIPE_TASTE = #{recipeTaste}, RECIPE_INGREDIENTS = #{recipeIngredients}, RECIPE_CONTENT = #{recipeContent}
WHERE RECIPE_NO = #{recipeNo}
